t.script:
document.addEventListener('DOMContentLoaded', function() {
    // Parse data from template
    var chartData = ;
    var statusData = ;
    var today = '';

    // Extract Bootstrap CSS variables for design token consistency
    var colorTokens = {
        primary: getComputedStyle(document.documentElement).getPropertyValue('--bs-primary').trim() || '#5470c6',
        success: getComputedStyle(document.documentElement).getPropertyValue('--bs-success').trim() || '#91cc75',
        warning: getComputedStyle(document.documentElement).getPropertyValue('--bs-warning').trim() || '#fac858',
        danger: getComputedStyle(document.documentElement).getPropertyValue('--bs-danger').trim() || '#ee6666',
        info: getComputedStyle(document.documentElement).getPropertyValue('--bs-info').trim() || '#409eff'
    };

    // Chart 1: Timeline (Gantt-style)
    var timelineChart = echarts.init(document.getElementById('chart_timeline'));
    var timelineOption = {
        tooltip: {
            trigger: 'axis',
            axisPointer: { type: 'shadow' }
        },
        legend: {
            data: ['Preparation', 'Review', 'Approval', 'Filing']
        },
        grid: {
            left: '3%',
            right: '4%',
            bottom: '3%',
            containLabel: true
        },
        xAxis: {
            type: 'time'
        },
        yAxis: {
            type: 'category',
            data: chartData.map(item => item.form)
        },
        series: [
            {
                name: 'Preparation',
                type: 'bar',
                stack: 'deadline',
                data: chartData.map(item => [item.prep_deadline, item.form]),
                itemStyle: { color: colorTokens.primary }
            },
            {
                name: 'Review',
                type: 'bar',
                stack: 'deadline',
                data: chartData.map(item => [item.review_deadline, item.form]),
                itemStyle: { color: colorTokens.success }
            },
            {
                name: 'Approval',
                type: 'bar',
                stack: 'deadline',
                data: chartData.map(item => [item.approval_deadline, item.form]),
                itemStyle: { color: colorTokens.warning }
            },
            {
                name: 'Filing',
                type: 'bar',
                stack: 'deadline',
                data: chartData.map(item => [item.filing_deadline, item.form]),
                itemStyle: { color: colorTokens.danger }
            }
        ]
    };
    timelineChart.setOption(timelineOption);

    // Chart 2: Completion vs Deadline
    var completionChart = echarts.init(document.getElementById('chart_completion'));
    var completionOption = {
        tooltip: {
            trigger: 'axis',
            axisPointer: { type: 'shadow' }
        },
        legend: {
            data: ['Completion %', 'Target (100%)']
        },
        xAxis: {
            type: 'category',
            data: chartData.map(item => item.form),
            axisLabel: {
                interval: 0,
                rotate: 45
            }
        },
        yAxis: {
            type: 'value',
            max: 100,
            axisLabel: {
                formatter: '{value}%'
            }
        },
        series: [
            {
                name: 'Completion %',
                type: 'bar',
                data: chartData.map(item => item.completion),
                itemStyle: {
                    color: function(params) {
                        return params.value >= 100 ? colorTokens.success :
                               params.value >= 50 ? colorTokens.warning : colorTokens.danger;
                    }
                }
            },
            {
                name: 'Target (100%)',
                type: 'line',
                data: chartData.map(() => 100),
                lineStyle: {
                    type: 'dashed',
                    color: colorTokens.info
                },
                itemStyle: { color: colorTokens.info }
            }
        ]
    };
    completionChart.setOption(completionOption);

    // Chart 3: Status Distribution (Pie)
    var statusChart = echarts.init(document.getElementById('chart_status'));
    var statusOption = {
        tooltip: {
            trigger: 'item',
            formatter: '{b}: {c} ({d}%)'
        },
        legend: {
            orient: 'vertical',
            left: 'left'
        },
        series: [
            {
                name: 'Status',
                type: 'pie',
                radius: '50%',
                data: statusData,
                emphasis: {
                    itemStyle: {
                        shadowBlur: 10,
                        shadowOffsetX: 0,
                        shadowColor: 'rgba(0, 0, 0, 0.5)'
                    }
                }
            }
        ]
    };
    statusChart.setOption(statusOption);

    // Populate data table
    var tableBody = document.getElementById('schedule_table_body');
    chartData.forEach(function(item) {
        var row = tableBody.insertRow();
        row.insertCell(0).textContent = item.form;
        row.insertCell(1).textContent = item.period;
        row.insertCell(2).textContent = item.filing_deadline;
        row.insertCell(3).textContent = item.completion.toFixed(1) + '%';
        row.insertCell(4).textContent = item.status.replace(/_/g, ' ').toUpperCase();
        row.insertCell(5).textContent = item.supervisor;
    });

    // Responsive resize
    window.addEventListener('resize', function() {
        timelineChart.resize();
        completionChart.resize();
        statusChart.resize();
    });
});
